mapper.select:
        SELECT SQL_CALC_FOUND_ROWS

        FROM major m
        WHERE m.major_id = m.major_id

            and m.major_id = #{majorId}


            and m.major_name = #{majorName}


            and m.dept_id = #{deptId}


            and m.dept_name = #{deptName}


            and m.school_id = #{schoolId}


            and m.school_name = #{schoolName}


            and m.teacher_id = #{teacherId}


            and m.teacher_name = #{teacherName}

        LIMIT #{pageNo},#{pageSize};
--         SELECT FOUND_ROWS() as total1;
