mapper.select:
        SELECT SQL_CALC_FOUND_ROWS

        FROM student s
        WHERE s.student_id = s.student_id

            and s.student_id = #{studentId}


            and s.student_name = #{studentName}


            and s.gender = #{gender}


            and s.major_id = #{majorId}


            and s.dept_id = #{deptId}


            and s.school_id = #{schoolId}

        LIMIT #{pageNo},#{pageSize};
--         SELECT FOUND_ROWS() as total1;

        SELECT SQL_CALC_FOUND_ROWS

        FROM student s
        LEFT JOIN major m
        ON s.major_id = m.major_id
        WHERE s.student_id = s.student_id

            and s.student_id = #{studentId}


            and s.student_name = #{studentName}


            and s.gender = #{gender}


            and s.major_id = #{majorId}


            and s.dept_id = #{deptId}


            and s.school_id = #{schoolId}

        LIMIT #{pageNo},#{pageSize};
--         SELECT FOUND_ROWS() as total1;
